export default async function olyxia(){const e=await fetch("https://olyxia.netlify.app/olyxia.min.json",{headers:{"Access-Control-Allow-Origin":"*"}}),t=await e.json();clients_length=t.olyxia.clients.length;let a=t.olyxia.clients,n=t.olyxia.employees;btn_change.onclick=()=>{if(btn_change.classList.contains("show_team")){n.map((e=>{team.insertAdjacentHTML("beforeend",`<div class="box mx-auto my-5 state_change">\n                <div class="imgbox mt-2">\n                    <img src="${e.image}" alt="team" class="d-block mx-auto bg-warning">\n                    <div class="details text-center bg-dark text-light mt-2 mx-auto">\n                        <h6>${e.name}</h6>\n                        <p>${e.position}</p>\n                    </div>\n                </div>\n            </div>`)}));btn_show_team.innerHTML="Close",btn_change.classList.replace("show_team","close_team")}else{const e=document.querySelectorAll(".state_change");for(let t of e)team.removeChild(t),btn_change.innerHTML="See All Team",btn_change.classList.replace("close_team","show_team"),team.scrollIntoView({behavior:"smooth",block:"center"})}};a.map((e=>{testimonial.insertAdjacentHTML("beforeend",`<div class="card-body bg-light card_width">\n                        <div class="card-head"> \n                            <img src='${e.avatar}' alt="Customers' Avatar" class="d-inline-block align-text-top bg-danger avatar">\n                            <div class="details">\n                                <div class="card-title">${e.name}</div>\n                                <div class="card-subtitle mb-2 text-muted">${e.title}</div>\n                            </div>\n                        </div>\n                        <div class="card-text">${e.testimony}</div>\n                    </div>`)}))}